import React from 'react';
import './booktile.css';
const BookTileComponent = (props) => {
    return(
       <div className="todotile">
            <h6 className="tileText">{props.bookItem.bookName}</h6>
            <hr/>
            <div>
                <i>{props.bookItem.yearOfRelease}</i>
                <span className="todooperation">
                    <input type="button" className="editbutton" onClick={() => props.updateBookItem(props.item)}/>
                </span>
            </div>
        </div>
    );
}


function mapDispatchToProps(dispatch, ownProps){
    return{
        addBookItem: (bookObj) => {
            dispatch(addBook(bookObj))
        },
        setAddEditFormVisibility: (visibilityInd) => {
            dispatch(setAddEditFormVisibility(visibilityInd))
        }
    }    
}



export default BookTileComponent;
