import React from 'react';
import {connect} from 'react-redux';
import {addBook} from '../actions';
import "./addbook.css";

class AddBookComponent extends React.Component {
    
    constructor(props){
        super(props);
        this.state = {showAddBookForm:false};
    }
    
    getBookObj = () => {
        return {
            id:Math.random(),
            bookName:"Santa Claus",
            yearOfRelease:"2018-10-11"
        }
    }
    
    addBookForm = () => {
        console.log("Hello World")
        
        return(
            <form className="addform">
                <p>Add Your Book</p>
                <div className="textInputGroup">
                    <input className="addBookInputText" placeholder="Book Name" type="text" />
                    <input className="addBookInputText" placeholder="Author Name" type="text" />
                    <input className="addBookInputText" title="release date" type="date" />
                </div>
                <button className="addbtn" onClick={(e) => {this.props.addBookItem(bookObj)}}type="button">Add book</button>
            </form>
        );
    }
    render(){
        return(
            <div>
                <h2>AddBookComponent</h2>
                <button type="button"
                    className="addBtn"
                    title="Add a Book"
                    onClick={(e)=>{
                        let bookObj={
                            id:Math.random(),
                            bookName:"Santa Claus",
                            yearOfRelease:"2018-10-11"
                        }
                        this.setState({showAddBookForm:true})
                        this.props.addBookItem(bookObj)
                    }}>+</button>
                    
                {this.state.showAddBookForm ? 
                    this.addBookForm() : false
                }
            </div>
        )
    }
};


function mapDispatchToProps(dispatch, ownProps){
    return{
        addBookItem: (bookObj) => {
            dispatch(addBook(bookObj))
        }
    }    
}

export default connect(null, mapDispatchToProps) (AddBookComponent);
