import React from 'react';
import "./addbook.css";

const AddEditFormComponent = (props) => {
    let bookName="";
    let yearOfRelease="";
    let bookAuthor="";
    props.actionType === "Edit" ? {bookName,yearOfRelease,author} = props.bookItem : "";
    return(
        <form className="addform">
                <div className="textInputGroup">
                    <input className="addBookInputText" 
                           placeholder="Book Name" 
                           onChange={(e) => {bookName=e.target.value}} type="text" 
                           defaultValue={bookName} 
                           required="true"/>
                    <input className="addBookInputText" 
                           placeholder="Author Name" 
                           onChange={(e) => {bookAuthor=e.target.value}} 
                           type="text" 
                           defaultValue={author} 
                           required="true"/>
                    <input className="addBookInputText" 
                           title="release date" 
                           onChange={(e) => {yearOfRelease=e.target.value}} 
                           type="date" 
                           defaultValue={yearOfRelease} 
                           required="true"/>
                </div>
                <button className="addbtn" onClick={(e) => {
                        props.addBookItem(props.getBookObj(bookName,bookAuthor,yearOfRelease))
                }} type="button">Add book</button>
        </form>
    )
}

export default AddEditFormComponent;
