import React from 'react';
import {connect} from 'react-redux';
//import {createStructuredSelector} from 'reselect';
import BookTileComponent from './BookTileComponent';
import SearchComponent from './SearchComponent';
//import makeSelectBookLibrary, {makeSelectBookList} from '../selectors';
import './bookgallery.css';
const BookGalleryComponent = (props) => {
    return(
        <div>
        <SearchComponent {...props}/>
        <div className="bookGallery">
            
            {props.bookList.map(bookItem => 
                <div key={bookItem.id}>
                    <BookTileComponent bookItem={bookItem}/>
                </div>    
            )}
        </div>
        </div>
    )
}
//const mapStateToProps = createStructuredSelector({bookLibrary: makeSelectBookLibrary(),bookList: makeSelectBookList()});
function mapStateToProps(state){
//    let { bookList, search }=state;
    let { bookList }=state;
    return {
        bookList
    }
}

export default connect(mapStateToProps,null)(BookGalleryComponent)
