import { ADD_BOOK, SEARCH_BOOK, SHOW_ADD_EDIT_FORM } from '../constants'; 

const initialState = {
    bookList:[{
      id:Math.random(), 
      bookName: "Theory of Computation",
      bookAuthor:"James",
      yearOfRelease:"2018-11-11"
    },{
      id:Math.random(), 
      bookName: "James Bond",
      author:"Andrew",
      yearOfRelease:"2018-11-11",
    },{
      id:Math.random(), 
      bookName: "Santa claus",
      bookAuthor:"James",
      yearOfRelease:"2018-11-11"
    },{
      id:Math.random(), 
      bookName: "Santa claus",
      bookAuthor:"James",
      yearOfRelease:"2018-11-11"
    }],
    search:"",
    addEditFormVisibility:false
}

export const reducer = (state=initialState,action) => {
    switch(action.type){
        case ADD_BOOK:  console.log(action.type, action.payload);
                        let bookItem = action.payload;
                        let { bookList } = state;
                        const newBookList = bookList.concat(bookItem);
                        return {...state, bookList: newBookList};
                        break;
        case SHOW_ADD_EDIT_FORM:    console.log(action.type, action.payload);
                                    return{...state, addEditFormVisibility:action.payload }
                                    break;
        case SEARCH_BOOK:   console.log(state.bookList, action.payload);
                            let search = action.payload;
                            const searchedBookList = state.bookList.filter(function(bookItem) {
	return bookItem.bookName;
});
                            return {...state, search, bookList: searchedBookList};
        default:console.log("hello");return state;
    }
}
