import React from 'react';
import {connect} from 'react-redux';
import {addBook} from '../actions';

const AddBookComponent = (props) => {
    let showAddBookForm = false;
    
    const addBookForm = () => {
        console.log("Hello World")
    }
    
    return(
        <div>
            <h2>AddBookComponent</h2>
            <button type="button" 
                onClick={(e)=>{
                    let bookObj={
                        id:Math.random(),
                        bookName:"Santa Claus",
                        yearOfRelease:"2018-10-11"
                    }
                    showAddBookForm = true;
                    props.addBookItem(bookObj)
                }}>click me</button>
            {showAddBookForm ? 
                addBookForm() : false
            }
        </div>
    )
};


function mapDispatchToProps(dispatch, ownProps){
    return{
        addBookItem: (bookObj) => {
            dispatch(addBook(bookObj))
        }
    }    
}

export default connect(null, mapDispatchToProps) (AddBookComponent);
