import React from 'react';
import {connect} from 'react-redux';
import {addBook, setAddEditFormVisibility} from '../actions';
import "./addbook.css";
import AddEditFormComponent from './AddEditFormComponent';


class AddBookComponent extends React.Component {
    
    constructor(props){
        super(props);
        this.state = {showAddBookForm:false};
    }
    
    getBookObj = (bookName,bookAuthor, yearOfRelease) => {
        return {
            id:Math.random(),
            bookName,
            bookAuthor,
            yearOfRelease
        }
    }
    
//    addBookForm = () => {
//        console.log("Hello World")
//        let bookName="";
//        let yearOfRelease="";
//        let bookAuthor="";
//        return(
//            <form className="addform">
//                <p>Add Your Book</p>
//                <div className="textInputGroup">
//                    <input className="addBookInputText" placeholder="Book Name" onChange={(e) => {bookName=e.target.value}} type="text" required="true"/>
//                    <input className="addBookInputText" placeholder="Author Name" onChange={(e) => {bookAuthor=e.target.value}} type="text" required="true"/>
//                    <input className="addBookInputText" title="release date" onChange={(e) => {yearOfRelease=e.target.value}} type="date" required="true"/>
//                </div>
//                <button className="addbtn" onClick={(e) => {
//                        this.props.addBookItem(this.getBookObj(bookName,bookAuthor,yearOfRelease))
//                        this.props.setAddEditFormVisibility(false)
//                        }} type="button">Add book</button>
//            </form>
//        );
//    }
    render(){
        let bookItem = {
              id:Math.random(), 
              bookName: "Santa claus",
              bookAuthor:"James",
              yearOfRelease:"2018-11-11"
        }
        return(
            <div>
                <button type="button"
                    className="addPlusSignBtn"
                    title="Add a Book"
                    onClick={(e)=>{
                        this.props.setAddEditFormVisibility(!this.props.showAddEditForm)
                    }}>+</button>
                    
                {this.props.showAddEditForm ? 
                    <AddEditFormComponent bookItem={bookItem} 
                                          addBookItem={this.props.addBookItem} 
                                          setAddEditFormVisibility={this.props.setAddEditFormVisibility}
                                          getBookObj={this.getBookObj}
                                          formType="Add"
                                          />: false
                }
            </div>
        )
    }
};


function mapDispatchToProps(dispatch, ownProps){
    return{
        addBookItem: (bookObj) => {
            dispatch(addBook(bookObj))
        },
        setAddEditFormVisibility: (visibilityInd) => {
            dispatch(setAddEditFormVisibility(visibilityInd))
        }
    }    
}

function mapStateToProps(state){
    return{
        showAddEditForm:state.addEditFormVisibility
    }
}

export default connect(mapStateToProps, mapDispatchToProps) (AddBookComponent);
