import {createSelector} from 'reselect';
import {fromJS} from 'immutable';

const makeSelectBookLibrary = (state) => state.get('bookLibrary');

const makeSelectBookList  = () => createSelector(
    makeSelectBookLibrary,
    (substate) => {
        const search = substate.get('search');
        return search.length<1
        ? substate.get('bookList')
        : substate.get('bookList').filter((obj) => {
            return bookList.get('bookName').toLoweCase().includes(search.toLowerCase())
        });
    }
);

export default makeSelectBookLibrary;

