import {ADD_BOOK, GET_BOOK_LIST, EDIT_BOOK_ITEM} from './constants';

export const addBook = (bookObj) => {
    return {
        type:ADD_BOOK,
        payload:bookObj
    }
}

export const getBookList = () => {
    return {
        type:GET_BOOK_LIST,
    }
}

export const editBookItem = (newBookObj) => {
    return {
        type:"EDIT_BOOK_ITEM",
        payload:newBookObj
    }
}

export const searchBook = (searchText) => {
    return {
        type:"SEARCH_BOOOK_ITEM",
        payload:searchText
    }
}
