import React from 'react';
import {connect} from 'react-redux';
import {createStructuredSelector} from 'reselect';
import BookTileComponent from './BookTileComponent';
import SearchComponent from './SearchComponent';
import selectBookLibrary, {selectBookList} from '../selectors';
import './bookgallery.css';
const BookGalleryComponent = (props) => {
    return(
        <div className="bookGallery">
            {/*<SearchComponent {...props}/>*/}
            {props.bookList.map(bookItem => 
                <div key={bookItem.id}>
                    <BookTileComponent bookItem={bookItem}/>
                </div>    
            )}
            
            {props.updateFormVisibility ? 
            <AddEditFormComponent bookItem={props.bookItem} 
                                  updateBookItem={props.updateBookItem} 
                                  setFormVisibility={props.setUpdateFormVisibility}
                                  formType="EDIT"
                                  />: false
            }
            
            
        </div>
    )
}

//const mapStateToProps (state)= createStructuredSelector({})
//    let { bookList, search }=state;
////    let { bookList }=state;
//    return {
//        bookList
//    }
//}



function mapDispatchToProps(dispatch, ownProps){
    return{
        updateBookItem: (bookObj) => {
            dispatch(updateBookItem(bookObj))
        },
        setUpdateFormVisibility: (visibilityInd) => {
            dispatch(setUpdateFormVisibility(visibilityInd))
        }
    }    
}


function mapStateToProps(state){
    return{
        updateFormVisibility:state.updateFormVisibility
    }
}
export default connect(mapStateToProps,updateBookItem)(BookGalleryComponent)
