import { ADD_BOOK, SEARCH_BOOK } from '../constants'; 

const initialState = {
    bookList:[]
}

export const reducer = (state=initialState,action) => {
    switch(action.type){
        case ADD_BOOK:    console.log(action.type, action.payload);
                            let bookItem = action.payload;
                            let { bookList } = state;
                            let newBookList = bookList.concat(bookItem);
                            return {...state, bookList: newBookList};
        case SEARCH_BOOK:   console.log(action.type, action.payload);
//                            let search = action.payload;
//                            const newBookList = state.book.filter((val) => val.includes(search));
//                            return {...state, value, works};
        default:return state;
    }
}
