import React from 'react';
import {connect} from 'react-redux';
import {addBook} from '../actions';
import "./addbook.css";

class AddBookComponent extends React.Component {
    
    constructor(props){
        super(props);
        this.state = {showAddBookForm:false};
    }
    
    getBookObj = (bookName,bookAuthor, yearOfRelease) => {
        return {
            id:Math.random(),
            bookName,
            bookAuthor,
            yearOfRelease
        }
    }
    
    addBookForm = () => {
        console.log("Hello World")
        let bookName="";
        let yearOfRelease="";
        let bookAuthor="";
        return(
            <form className="addform">
                <div className="textInputGroup">
                    <input className="addBookInputText" placeholder="Book Name" onChange={(e) => {bookName=e.target.value}} type="text" required="true"/>
                    <input className="addBookInputText" placeholder="Author Name" onChange={(e) => {bookAuthor=e.target.value}} type="text" required="true"/>
                    <input className="addBookInputText" title="release date" onChange={(e) => {yearOfRelease=e.target.value}} type="date" required="true"/>
                </div>
                <button className="addbtn" onClick={(e) => {
                        this.props.addBookItem(this.getBookObj(bookName,bookAuthor,yearOfRelease))
                        this.setState({showAddBookForm:false})
                        }} type="button">Add book</button>
            </form>
        );
    }
    render(){
        return(
            <div>
                <button type="button"
                    className="addBtn"
                    title="Add a Book"
                    onClick={(e)=>{
                        this.setState({showAddBookForm:true})
                    }}>+</button>
                    
                {props.showAddEditForm ? 
                    this.addBookForm() : false
                }
            </div>
        )
    }
};


function mapDispatchToProps(dispatch, ownProps){
    return{
        addBookItem: (bookObj) => {
            dispatch(addBook(bookObj))
        },
        seAddEditFormVisibility: (visibilityInd) => {
            dispatch(seAddEditFormVisibility)
        }
    }    
}

function mapStateToProps(state){
    return{
        showAddEditForm:state.addEditFormVisibility
    }
}

export default connect(null, mapDispatchToProps) (AddBookComponent);
